import React from 'react';
import { Route,Switch, Redirect } from 'react-router-dom';
import  SignIn  from './authorization/sign_in';
import  SignUp  from './authorization/sing_up/index';
import  ResetPassword  from './authorization/reset_password/index';
import DisplayTodo from './todo-list/display-todo';
import { connect } from 'react-redux';

export const Root = () => (
        <Switch>
            <Route exact path = "/">
                <SignIn />
            </Route>
            <Route path = "/registration">
                <SignUp />
            </Route>
            <Route path = "/reset">
                <ResetPassword />
            </Route>
            <PrivateRoute path = "/todo/:pk">
                <DisplayTodo />
            </PrivateRoute> 
        </Switch>
);

const mapStateToProps = (state) => ({
    isLoggedIn : state.user_data.isLoggedIn
})

function PrivateRoute({children, ...rest}, {...props}) {
    console.log(children);
    console.log(props)
    return(
        <Route
            {...rest}
            render= {({ location }) => 
                isLoggenIn ? 
                (
                    children
                ):(
                    <Redirect 
                        to= {{
                            pathname: '/',
                            state : {from : location}
                        }}
                    />
                )
            }
        />
    );
}

connect(mapStateToProps, null)(PrivateRoute);
