import React, { useState } from 'react';
import DeleteIcon from '@material-ui/icons/Delete';
import { makeStyles, Typography, Card, CardContent, IconButton, Checkbox} from '@material-ui/core';
import { ITask } from '../../../../interfaces/ITask';
import  FormDialog  from '../../../../components/FormDialog';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { show } from 'redux-modal'

const useStyles = makeStyles((theme) => ({

    root: {
        display: 'flex',
        paddingRight : theme.spacing(3),
        margin : theme.spacing(1),
      },
      notCheck :{
        flex: '1 0 auto',
      },
      check: {
        flex: '1 0 auto',
        textDecoration : 'line-through',
        color: 'rgba(0, 0, 0, 0.12)',
        boxShadow: 'none',
      },
}))

interface TodoListProps {
    idTodo : number;
}

export const ListTask : React.FC<TodoListProps> = ({idTodo, ...props}) => {

    const classes = useStyles();
    let removeTask : ITask; 
    const taskList : ITask[] = [
        {
            id: 7,
            idTodo : 1,
            title : 'покормить кота',
            isCompleted : false,
        },
        {
            id: 5,
            idTodo : 1,
            title : 'покормить себя',
            isCompleted : false,
        },
        {
            id: 6,
            idTodo : 1,
            title : 'покормить брата',
            isCompleted : false,
        },
    ];

    const[open, setOpen] = useState(true);

    const handleClose = () => {
        setOpen(false)
    }
    // const handleOpen = name => () => {
    //     props.show(name, { message: `This is a ${name} modal` })
    //   };
    const handleRemoveTask = ( task : ITask) => {
        // onRemove(task)
        console.log('Задача удалена : ', task)
        setOpen(false)
    }

    const handleOpen = (task : ITask) => {
        setOpen(true)
        removeTask = task
        console.log(task)

    }
    return(
        <div>
            {taskList.map((todo) => {
                let classCheked = classes.notCheck
                if(todo.isCompleted){
                    classCheked = classes.check
                }
                if(todo.idTodo === idTodo){
                    return(
                        <Card key = {todo.id}  className = {classes.root}>
                            <Checkbox
                                checked = {todo.isCompleted}
                                name = 'checkBox_toggle_task'/>
                            <CardContent className = {classCheked}>
                                <Typography variant = "h6" component = "h6">
                                    {todo.title}
                                </Typography>
                            </CardContent>
                            <IconButton aria-label = "delete" color="secondary" edge="end" onClick = {() => handleOpen(todo)}>
                                <DeleteIcon fontSize = "small"/>
                            </IconButton>
                            <FormDialog 
                                removeElement = {removeTask}
                                isOpen = {open}

                            />
                        </Card>
                    );
                }
                })}
        </div>
    )
}

export default ListTask
