import { fork } from 'redux-saga/effects'
import { watchCreateTask, watchUpdateTask } from '../ pages/User'
import { combineReducers } from 'redux'


export function* rootSaga(){
    yield[
        fork(watchCreateTask),
        fork(watchUpdateTask),
    ]
}
