import React from "react";
import { Field, InjectedFormProps, reduxForm } from "redux-form";
import { renderTextField } from "../../../../components/TextField";
import { Button, makeStyles, Grid } from "@material-ui/core";
import { RouteComponentProps, withRouter } from "react-router-dom";
import DateFnsUtils from '@date-io/date-fns';
import { KeyboardDatePicker, MuiPickersUtilsProvider } from '@material-ui/pickers';
import { ITask } from "../../../../interfaces/ITask";
import { ITodo } from "../../../../interfaces/ITodo";


interface IProps extends RouteComponentProps<{pk:string}>{
    onAddTask(newTask: ITask) : void;
    todoList : ITodo[];
    onCreateTodo(sortTodo : ITodo) : void;
}

const useStyles = makeStyles((theme) => ({
    submit : {
        margin: theme.spacing(3.5,0,0,5),
        textAlign: 'center',
    },
    textField :{
        textAlign: 'center',
        marginLeft: theme.spacing(3),
        color: theme.palette.text.secondary,
    },
    dateGrid: {
        marginLeft: theme.spacing(5),
        textAlign: 'center',
    },
}));

const Form : React.FC<IProps & InjectedFormProps<{}, IProps>> = ({onAddTask, onCreateTodo, todoList, history , ...props}) => {

    const classes = useStyles();
    const { pk } : any = props.match.params;

    const [selectedDate, setSelectedDate] = React.useState(new Date());

    const handleDateChange = (date) => {
        setSelectedDate(date);
    };

    const submit = values => {

        const newTask : ITask = {
            id : null,
            idTodo: 0,
            title : values.task,
            isCompleted : false
        };
        const month = selectedDate.getMonth() + 1;
        const date = selectedDate.getDate().toString() + "." +  month.toString() + "." + selectedDate.getFullYear().toString();
        if(todoList.co)
        onAddTask(newTask)

    };

    return(
        <form onSubmit = {props.handleSubmit(submit)}>
            <Grid container
                direction="row"
                justify="center"
                alignItems="flex-start">
                    <Grid item xs >
                        <Field name = "task" component = {renderTextField} label = "Наименование задачи" className = {classes.textField}/>
                    </Grid>
                    <MuiPickersUtilsProvider utils={DateFnsUtils}>
                        <Grid item className = {classes.dateGrid}>
                            <KeyboardDatePicker
                                margin="normal"
                                id="date-picker-dialog"
                                label="Выберите дату: "
                                format="dd.MM.yyyy"
                                value={selectedDate}
                                onChange={handleDateChange}
                                KeyboardButtonProps={{
                                    'aria-label': 'change date',
                                }}
                            />
                        </Grid>
                    </MuiPickersUtilsProvider>
                    <Grid item xs={3}>
                        <Button 
                            name= "create_task"
                            type="submit"
                            variant = "contained"
                            color = "primary"
                            className = {classes.submit}>
                                Создать
                        </Button>
                    </Grid>
            </Grid>    
        </form>
    );
}

const form = reduxForm<{}, IProps>({
    form : 'createTask'
})(Form);

export default withRouter(form);
