import React from 'react';
import { makeStyles, Typography } from '@material-ui/core';
import Form from './components/form_sign_in';
import { onLoginUserAction, onGetTokenAction } from '../../../store/actions';
import { connect } from 'react-redux';
import { IUser } from '../../../interfaces/IUser';
import  SignUp  from '../../authorization/sing_up/index';
import  ResetPassword  from '../../authorization/reset_password/index';
import { Switch, Route, RouteComponentProps, withRouter } from 'react-router-dom';
import DisplayTodo from '../../todo-list/display-todo';


interface IProps extends RouteComponentProps{
    onLoginUser(user : IUser, history) : void;
    onGetToken() : void;
    isErrorAuth : boolean;
}

const useStyles = makeStyles((theme) => ({

    page: {
        marginTop: theme.spacing(8),
        display : 'flex',
        flexDirection: 'column',
        alignItems : 'center',
    },
}));

const mapDispatchToProps = (dispatch) => (
    { 
        onLoginUser : (user : IUser, history) => {
            dispatch(onLoginUserAction(user, history))
        },
        onGetToken : () => { 
            dispatch(onGetTokenAction()) 
        },
    }
)

const mapStateToProps = (state) => ({
    isErrorAuth : state.user_data.isErrorAuth
})


export const SignIn : React.FC<IProps> = (props : IProps) => {

    const classes = useStyles();
    const {path} = props.match;
    return (
        <div className = {classes.page}>
            <Switch>
                <Route path = "/registration">
                    <SignUp />
                </Route>
                <Route path = "/reset">
                    <ResetPassword />
                </Route>
                <Route path = "/todo/:pk">
                    <DisplayTodo />
                </Route> 
            </Switch>
            <Typography variant = 'h5' component = "h1">
                Авторизация
            </Typography>
            <Form onLoginUser = {props.onLoginUser} isErrorAuth = {props.isErrorAuth}/>
        </div>
    );
}

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(SignIn));
