import { getCookie } from "./cookie";
import { IUser } from "../interfaces/IUser";


const csrftoken = getCookie('csrftoken');

const dataFetch = (url : string, data) => {
    return (
        new Request(url,{
            mode : 'cors',
                headers: {
                    'Content-type' : 'application/json',
                    'X-CSRFToken' : csrftoken!,
                },
            method : 'POST',
            body : JSON.stringify(data)
        })
    );
}

export async function fetchLoginUser(login_user){

    // console.log(dataFetch)
    const response = fetch(dataFetch('http://127.0.0.1:8000/api/v1/token/auth/', login_user))

    const data = response.then(response =>
        response.json()
    ).catch(error => {
        console.log('ERROR_FETCH: ', error);
    });

    return await data;
}
export type Empty = {}
type Query = Record<string, any>;
type Payload = Record<string, any>;
type Response = Record<string, any>;

interface RequestData<P extends Payload, Q extends Query> extends RequestInit{
    user? : P;
    query? : Q;
}

interface ResponseBody<R extends Response > {
    data?: R;
    status: number;
    statusText: string;
    headers: Headers;
    code?: number;
}

interface ResponseData<R extends Response >{ 
    response?: ResponseBody<R>;
    error?: any;
}


const prepareBodyRequest = <T extends Payload>(payload: T) : string => {
    return JSON.stringify(payload);
}

const prepareHeadersRequest = (token: string) : Record<string, string> => {
    return {'' : ''};
}

const request = <P extends Payload, R extends Response,Q extends Query>(
    url : string,
    data: RequestData<P, Q>,
    token? : string,
) : Promise<ResponseData<R>> => {

    if(data.user){
        data.body = prepareBodyRequest<Payload>(data.user);
    }
    data.mode = 'cors';
    data.headers = prepareHeadersRequest({token})

    return fetchLoginUser('')
}

interface TokenResponse{
    token : string;
}
export const signIn =(user : IUser) => request<IUser, TokenResponse, Empty>(
    'token/auth/', {method : 'POST',  user},
);
