import React from'react';
import { Grid, Button, makeStyles } from '@material-ui/core';
import { Field, submit, reduxForm } from 'redux-form';
import { renderTextField } from '../../../components/TextField';


const useStyles = makeStyles((theme) => ({
    field: {
        display: 'block',
    },
    cardGrid: {
        marginLeft : theme.spacing(3),
        width : '90%'
    },
}))

const CreateTask : React.FC = () => {

    const submit = values => {
        console.log('создана задача ', values)
    };
    return(
        <form onSubmit = {() => submit}>
            <Grid
                container
                direction="row"
                justify="flex-start"
                alignItems="center"
            > 
                <Grid item xs>
                    <Field component= {renderTextField} name = "title" label = "title"/>
                </Grid>
                <Grid item xs={3}>
                    <Button size = "small" color = "secondary"> Добавить задачу</Button>
                </Grid>
            </Grid>
        </form>
    );
}

export default reduxForm({
    form: 'create-task'
})(CreateTask);
