import { getCookie } from "./cookie";
import { IUser } from "../interfaces/IUser";


const csrftoken = getCookie('csrftoken');

const dataFetch = {
    mode : 'cors',
    headers: {
        'Content-type' : 'application/json',
        'X-CSRFToken' : csrftoken!,
    }
}
export async function fetchCreateUser(user : IUser){

    const create_user = {
        username : user.username,
        password : user.password!,
        email : user.email!
    };

    console.log('CREATE_USER: ', create_user);


    const response = fetch('http://127.0.0.1:8000/api/v1/users/create/', {

        method : 'POST',

        body : JSON.stringify(create_user)

    })
    const data = response.then(respon => respon.json())
    .catch(error => 
