import React from 'react';
import { makeStyles, Typography, Button, AppBar, Toolbar, Grid } from '@material-ui/core';
import FormCreateTask from './components/formCreateTask';
import { connect } from 'react-redux';
import { addTaskAction, onLogoutAction } from '../../../store/actions';
import { reset } from 'redux-form';
import { ITask } from '../../../interfaces/ITask';
import { ITodo } from '../../../interfaces/ITodo';
import CardTodo from './components/cardList';
import { withRouter, RouteComponentProps } from 'react-router-dom';


interface IProps extends RouteComponentProps{
    onAddTask(newTask : ITask) : void;
    onLogout(): void;
}

const useStyles = makeStyles((theme) => ({

    root : {
        flexGrow : 1,
    },
    title : {
        flexGrow: 1,
    },
    cardGrid: {
        marginLeft : theme.spacing(3),
        width : '90%'
    },
    cardMedia: {
        display: 'block',
    },
}))

const mapDispatchToProps = (dispatch) => {
    return({
        onAddTask : (newTask : ITask) => {
            dispatch(addTaskAction(newTask))
            dispatch(reset('createTask'))
        },
        onLogout : () => { 
            dispatch(onLogoutAction()) 
        }
    });
}

const mapStateToProps = (state) => ({})

export const DisplayTodo : React.FC<IProps> = ({onAddTask, onLogout, history} : IProps) => {
    const classes = useStyles();
    const todoList : ITodo[]= [
        {
            id:1,
            user:1,
            title : '1.8.2020'
        }, 
        {
            id:3,
            user:1,
            title : '2.8.2020'
        },
        {
            id:2,
            user:1,
            title : '2.8.2020'
        }
    ]

    const handleLogout = () => {
        localStorage.removeItem('token')
        onLogout()
        history.push('/')
    }

    return(
        <div className = {classes.root}>
            <AppBar position = "static">
                <Toolbar>
                    <Typography variant="h6" className = {classes.title}>
                        username
                    </Typography>
                    <Button color="inherit" onClick = {() => handleLogout()}>
                        Выход
                    </Button>
                </Toolbar>
            </AppBar>
            <FormCreateTask onAddTask = {onAddTask}/>
            <CardTodo todoList = {todoList}/>

        </div>
    )
}

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(DisplayTodo))
