import React from 'react';
import { makeStyles, Typography, Button, AppBar, Toolbar, Grid} from '@material-ui/core';
import FormCreateTask from './components/formCreateTask';
import { connect } from 'react-redux';
import { addTaskAction } from '../../../store/actions';
import { reset } from 'redux-form';
import { ITask } from '../../../interfaces/ITask';


interface IProps{
    onAddTask(newTask : ITask) : void;
}

const useStyles = makeStyles((theme) => ({

    root : {
        flexGrow : 1,
    },
    title : {
        flexGrow: 1,
    },
    submit : {
        margin: theme.spacing(3.5, 0, 0, 2),
        textAlign: 'center',
    },
    textField :{
        textAlign: 'center',
        marginLeft: theme.spacing(3),
        color: theme.palette.text.secondary,
    },
}))

const mapDispatchToProps = (dispatch) => {
    return({
        onAddTask : (newTask : ITask) => {
            dispatch(addTaskAction(newTask))
            dispatch(reset('createTask'))
        }
    })
}

const mapStateToProps = (state) => ({})

export const DisplayTodo : React.FC<IProps> = (props : IProps) => {
    const classes = useStyles();

    return(
        <div className = {classes.root}>
            <AppBar position = "static">
                <Toolbar>
                    <Typography variant="h6" className = {classes.title}>
                        username
                    </Typography>
                    <Button color="inherit">
                        Выход
                    </Button>
                </Toolbar>
            </AppBar>
            <FormCreateTask onAddTask = {props.onAddTask}/>
            <div className={classes.root}>
                <GridList cellHeight={180} className={classes.gridList}>
                    <GridListTile key="Subheader" cols={2} style={{ height: 'auto' }}>
                        <ListSubheader component="div">December</ListSubheader>
                    </GridListTile>
                        {tileData.map((tile) => (
                            <GridListTile key={tile.img}>
                                <img src={tile.img} alt={tile.title} />
                                <GridListTileBar
                                    title={tile.title}
                                    subtitle={<span>by: {tile.author}</span>}
                                    actionIcon={
                                        <IconButton aria-label={`info about ${tile.title}`} className={classes.icon}>
                                            <InfoIcon />
                                        </IconButton>
                                    }
                                />
                            </GridListTile>
                        ))}
      </GridList>
    </div>

        </div>
    )
}


export default connect(mapStateToProps, mapDispatchToProps)(DisplayTodo)
