     }

    return(
            <div className = {classes.page}>
                <Typography variant = 'h5' component = "h1">
                    Сброс пароля
                </Typography>
                <form onSubmit={props.handleSubmit(handleSubmit)}>
                    <Field name="email" component={renderTextField} label="email" className = {classes.textField}/>
                    <Button
                        type="submit"
                        fullWidth
                        variant = "contained"
                        color = "primary"
                        className = {classes.submit}>
                         Отправить 
                    </Button>
                    <Grid 
                        container
                        direction="row"
                        justify="flex-end"
                        alignItems="baseline">
                            <NavLink to="/" >
                               Авторизация
                            </NavLink>
                    </Grid>
                </form>
            </div>
    )
}

const form = reduxForm<{}, IProps>({
    form : 'resetPassword'
})(ResetPassword);

export default form;
