import React, { useState } from 'react';
import DeleteIcon from '@material-ui/icons/Delete';
import { makeStyles, Typography, Card, CardContent, IconButton, Checkbox} from '@material-ui/core';

import { RouteComponentProps } from 'react-router';
import {  withRouter } from 'react-router-dom';
import { InjectedFormProps } from 'redux-form';
import { ITask } from '../../../../interfaces/ITask';



const useStyles = makeStyles((theme) => ({

    root: {
        display: 'flex',
        padding : theme.spacing(0,3,0,3),
        margin : theme.spacing(5),
      },
      details: {
        display: 'flex',
        flexDirection: 'column',
      },
      notCheck :{
        flex: '1 0 auto',
      },
      check: {
        flex: '1 0 auto',
        textDecoration : 'line-through',
        color: 'rgba(0, 0, 0, 0.12)',
        boxShadow: 'none',
      },
}))

interface TodoListProps {
    idTodo : number;
}

export const ListTask : React.FC<TodoListProps> = (idTodo: any) => {

    const classes = useStyles();
    const taskList : ITask[] = [
        {
            id: 0,
            idTodo : '1',
            title : 'покормить кота',
            isCompleted : false,
        },
        {
            id: 1,
            idTodo : 1,
            title : 'покормить кота',
            isCompleted : false,
        },
        {
            id: 2,
            idTodo : 1,
            title : 'покормить кота',
            isCompleted : false,
        },
    ]
    return(
        <div>
            {taskList.map((todo) => {
                let classCheked = classes.notCheck
                if(todo.isCompleted){
                    classCheked = classes.check
                }
                if(todo.idTodo !== idTodo){
                    console.log('true')
                }
                    return(
                        <Card key = {todo.id}  className = {classes.root}>
                            <Checkbox
                                checked = {todo.isCompleted}
                                name = 'checkBox_toggle_task'/>
                            <CardContent className = {classCheked}>
                                <Typography variant = "h6" component = "h6">
                                    {todo.title}
                                </Typography>
                            </CardContent>
                            <IconButton aria-label = "delete" color="secondary" edge="end">
                                <DeleteIcon fontSize = "small"/>
                            </IconButton>
                            {/* <FormDialog todo = {task} handlerRemove = {handleRemoveTask} show = {open} handleDestroy = {handleClose} handleHide = {handleClose} /> */}
                        </Card>
                    );
                })}
        </div>
    )
}

export default ListTask
