import { CREATE_TASK, REMOVE_TASK, TOGGLE_TASK } from "../../actions/types";
import  { ITask }  from "../../../interfaces/ITask";


const initialStateUser = {
    tasks : []
};

export function task_reducer ( state = initialStateUser, action)  {
    switch(action.type){
        case CREATE_TASK:
            return {...state,
                tasks : [
                    ...state.tasks,
                    {
                        title: action.newTask.title,
                        is_completed : action.newTask.is_completed
                    }
                ]
            };
        case TOGGLE_TASK:
            return { ...state, 
                tasks: [
                    ...state.tasks,
                    state.tasks.map((task, index) => {
                    if(index === action.task.id){
                        return {task,
                            is_completed : action.task.is_completed
                        };
                    }
                    return task;
                })
            ]
            };
        case REMOVE_TASK:
            return Object.assign({}, state, {
                tasks: state.tasks.filter((item : ITask)=> item.id !== action.task.id)
            });
        default : return state;
    }
}
