import React from "react";
import { Field, InjectedFormProps, reduxForm } from "redux-form";
import { RenderTextField } from "../../../../components/TextField";
import { Button, makeStyles, Grid } from "@material-ui/core";
import { NavLink } from "react-router-dom";
import Alert from "../../../../components/Alert/index";


interface IProps {
    isErrorAuth : boolean;
    error : object;
}

const useStyles = makeStyles((theme) => ({
    submit : {
        margin: theme.spacing(3, 0, 2),
    },
}));

const Form : React.FC<IProps & InjectedFormProps<{}, IProps>> = ({ isErrorAuth, error, ...props}) => {

    const classes = useStyles();
    const errorText = () => {
        let error = '';
        if(error){
            if(error.username && error.password !== undefined){
                error = "Такой пользователь существует. Пароль слишком легкий.";
            }else if (data.password !== undefined){
                error = "Пароль слишком легкий.";
            }else if (data.username !== undefined){
                error = "Такой пользователь существует.";
            }
        }

        return error;
    }
    return(
        <form onSubmit={props.handleSubmit}>
            <Field name="username" component={RenderTextField} label="username" type= "username"/>
            <Field name="email" component={RenderTextField} label="email" type= "email"/>
            <Field name="password" component={RenderTextField} label="password" type= "password"/>
            <Button
                type="submit"
                fullWidth
                variant = "contained"
                color = "primary"
                className = {classes.submit}>
                    Зарегистрироваться
            </Button>
            <Grid 
                container
                direction="row"
                justify="flex-end"
                alignItems="baseline">
                    <NavLink to="/" >
                        Авторизация
                    </NavLink>
            </Grid>
            {isErrorAuth && <Alert error_text = {errorText}/>}
        </form>
    );
}

const form = reduxForm<{}, IProps>({
    form : 'sign-up'
})(Form);

export default form;
