import { getCookie } from "./cookie";
import { IUser } from "../interfaces/IUser";
import { baseUrl } from "./baseUrlRequest";


const csrftoken = getCookie('csrftoken');

const dataFetch = (url : string, data) => {
    return (
        new Request(url,{
            mode : 'cors',
                headers: {

                    'X-CSRFToken' : csrftoken!,
                },
            method : 'POST',
            body : JSON.stringify(data)
        })
    );
}

export async function fetchLoginUser(login_user){

    // console.log(dataFetch)
    const response = fetch(dataFetch('http://127.0.0.1:8000/api/v1/token/auth/', login_user))

    const data = response.then(response =>
        response.json()
    ).catch(error => {
        console.log('ERROR_FETCH: ', error);
    });

    return await data;
}
export type Empty = {}
type Payload = Record<string, any>;
type Response = Record<string, any>;

interface RequestData<P extends Payload> extends RequestInit{
    user? : P;
}

interface ResponseBody<R extends Response > {
    data?: R;
    status: number;
    statusText: string;
    headers: Headers;
    code?: number;
}

interface ResponseData<R extends Response >{ 
    response?: ResponseBody<R>;
    error?: any;
}


const prepareBodyRequest = <T extends Payload>(payload: T) : string => {
    return JSON.stringify(payload);
}

const prepareHeadersRequest = (token?: string) : Record<string, string> => {

    let headers : Record<string, string>;
    headers = {
        'Content-type' : 'application/json',
    }
    if(token) {
        headers = {
            ...headers,
            Authorization : `JWT ${token}`
        }
    }
    return headers;
}

const getResponseBody = <R extends Response>(response : R) : Promise<ResponseBody<R>> => {
    let data;
    if(response.status)
    data = response.json()
    return {response};

}

const request = async <P extends Payload, R extends Response>(
    url : string,
    data: RequestData<P>,
    token? : string,
) : Promise<ResponseData<R>> => {

    if(data.user){
        data.body = prepareBodyRequest<Payload>(data.user);
    }
    data.mode = 'cors';
    data.headers = prepareHeadersRequest(token);
    const requestUrl = baseUrl + url;
    let response;

    try{
        response = await fetch(requestUrl, data);
    }catch(error){
        console.log("Что-то не так с сетью");
    }

    const responseBody = await getResponseBody<R>(response);
    console.log(responseBody);
    return fetchLoginUser('')
}

interface TokenResponse{
    token : string;
}
export const signIn =(user : IUser) => request<IUser, TokenResponse >(
    'token/auth/', {method : 'POST',  user},
);
