import React, { useEffect } from 'react';
import { makeStyles, Typography } from '@material-ui/core';
import Form from './components/form_sign_in';
import { onLoginUserAction, onGetTokenAction, onCurrentUserAction, onLogoutAction } from '../../../store/actions';
import { connect } from 'react-redux';
import { IUser } from '../../../interfaces/IUser';
import { fetchGetDataUser } from '../../../services/services_user';
import { RouteComponentProps, withRouter } from 'react-router-dom';


interface IProps extends RouteComponentProps{
    onLoginUser(user : IUser, history) : void;
    onGetToken() : void;
    onLogout() : void;
    onCurrentUser (user : IUser) : void;
    isErrorAuth : boolean;
    isLoggedIn: boolean;
    id: number;
}

const useStyles = makeStyles((theme) => ({

    page: {
        marginTop: theme.spacing(8),
        display : 'flex',
        flexDirection: 'column',
        alignItems : 'center',
    },
}));

const mapDispatchToProps = (dispatch) => (
    { 
        onLoginUser : (user : IUser, history) => {
            dispatch(onLoginUserAction(user, history))
        },
        onGetToken : () => { 
            dispatch(onGetTokenAction()) 
        },
        onCurrentUser : (user : IUser) => {
            dispatch(onCurrentUserAction(user))
        },
        onLogout : () => { 
            dispatch(onLogoutAction()) 
        }
    }
)

const mapStateToProps = (state) => ({
    isErrorAuth : state.user_data.isErrorAuth,
    isLoggedIn : state.user_data.isLoggedIn,
    id : state.user_data.id
})


export const SignIn : React.FC<IProps> = ({history, ...props} : IProps) => {

    const classes = useStyles();
    if (localStorage.getItem('token')){
        props.onGetToken();
    }

    useEffect(() => {
        if(props.isLoggedIn){
            try{
                const result = fetchGetDataUser();
                result.then((user) => {
                    if(user.id !== undefined){
                        props.onCurrentUser(user);
                    }else{
                        props.onLogout();
                    }
                });
                console.log()
                if(props.isLoggedIn){
                    history.push(`/todo/${props.id}`);
                }
            }catch(error){
                console.log('ERROR: ', error);
            }
        }
    }, [props.isLoggedIn]);


    return (
        <div className = {classes.page}>
            <Typography variant = 'h5' component = "h1">
                Авторизация
            </Typography>
            <Form onLoginUser = {props.onLoginUser} isErrorAuth = {props.isErrorAuth}/>
        </div>
    );
}

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(SignIn));
