import React from 'react';
import { makeStyles, Typography, Button, AppBar, Toolbar, Grid, GridList, GridListTile, ListSubheader, GridListTileBar, IconButton, Container, Card, CardMedia, CardContent, CardActions} from '@material-ui/core';
import FormCreateTask from './components/formCreateTask';
import { connect } from 'react-redux';
import { addTaskAction } from '../../../store/actions';
import { reset } from 'redux-form';
import { ITask } from '../../../interfaces/ITask';


interface IProps{
    onAddTask(newTask : ITask) : void;
}

const useStyles = makeStyles((theme) => ({

    root : {
        flexGrow : 1,
    },
    title : {
        flexGrow: 1,
    },
    submit : {
        margin: theme.spacing(3.5, 0, 0, 2),
        textAlign: 'center',
    },
    textField :{
        textAlign: 'center',
        marginLeft: theme.spacing(3),
        color: theme.palette.text.secondary,
    },
    cardGrid: {
        paddingBottom: theme.spacing(8),
    },
    card: {
        display: 'flex-start',
        flexDirection: 'column',
    },
    cardMedia: {
        paddingTop: '56.25%', // 16:9
    },
    cardContent: {
        flexGrow: 1,
    },
}))

const mapDispatchToProps = (dispatch) => {
    return({
        onAddTask : (newTask : ITask) => {
            dispatch(addTaskAction(newTask))
            dispatch(reset('createTask'))
        }
    })
}

const mapStateToProps = (state) => ({})

export const DisplayTodo : React.FC<IProps> = (props : IProps) => {
    const classes = useStyles();

    const todoList = [
        {
            id: 0,
            title : 'покормить кота',
            isCompleted : false,
        }
    ]
    return(
        <div className = {classes.root}>
            <AppBar position = "static">
                <Toolbar>
                    <Typography variant="h6" className = {classes.title}>
                        username
                    </Typography>
                    <Button color="inherit">
                        Выход
                    </Button>
                </Toolbar>
            </AppBar>
            <FormCreateTask onAddTask = {props.onAddTask}/>
                {/* End hero unit */}
                <Grid container
                    direction = 'row'
                    justify = 'flex-start'
                    alignItems = 'flex-start' 
                    spacing = {1}
                    >
                    {todoList.map((todo) => (
                    <Grid item key={todo.id} xs={12} md={3}>
                        <Card className={classes.card}>
                            <CardMedia
                                className={classes.cardMedia}
                                image="https://source.unsplash.com/random"
                                title="Image title"
                            />
                            <CardContent className={classes.cardContent}>
                                <Typography gutterBottom variant="h5" component="h2">
                                    {todo.title}
                                </Typography>
                                <Typography>
                                    {todo.isCompleted}
                                </Typography>
                            </CardContent>
                            <CardActions>
                                <Button size="small" color="primary">
                                    View
                                </Button>
                                <Button size="small" color="primary">
                                    Edit
                                </Button>
                            </CardActions>
                        </Card>
                    </Grid>
                    ))}
                </Grid>
        </div>
    )
}


export default connect(mapStateToProps, mapDispatchToProps)(DisplayTodo)
