import React from 'react';
import { makeStyles, Typography } from '@material-ui/core';
import Form from './components/form_sign_in';
import { onLoginUserAction, onGetTokenAction } from '../../../store/actions';
import { connect } from 'react-redux';
import { IUser } from '../../../interfaces/IUser';


interface IProps{
    onLoginUser(user : IUser, history) : void;
    onGetToken() : void;
    isErrorAuth : boolean;
    isLoggedIn: boolean;
}

const useStyles = makeStyles((theme) => ({

    page: {
        marginTop: theme.spacing(8),
        display : 'flex',
        flexDirection: 'column',
        alignItems : 'center',
    },
}));

const mapDispatchToProps = (dispatch) => (
    { 
        onLoginUser : (user : IUser, history) => {
            dispatch(onLoginUserAction(user, history))
        },
        onGetToken : () => { 
            dispatch(onGetTokenAction()) 
        },
    }
)

const mapStateToProps = (state) => ({
    isErrorAuth : state.user_data.isErrorAuth,
    isLoggedIn : state.user_data.isLoggedIn
})


export const SignIn : React.FC<IProps> = (props : IProps) => {

    const classes = useStyles();
    if (localStorage.getItem('token')){
        props.onGetToken();
    }

    useEffect(() => {
        if(current_state_user){
            try{
                    const result = fetchGetDataUser()

                    // if(current_user.id !== undefined){
                    //     props.onCurrentUser(current_user);
                    // }else{
                    //     props.onLogout();
                    // }
            }catch(error){
                console.log('ERROR: ', error)
            }
        }
    }, [current_state_user])
    return (
        <div className = {classes.page}>
            <Typography variant = 'h5' component = "h1">
                Авторизация
            </Typography>
            <Form onLoginUser = {props.onLoginUser} isErrorAuth = {props.isErrorAuth}/>
        </div>
    );
}

export default connect(mapStateToProps, mapDispatchToProps)(SignIn);
