import React from 'react';
import { makeStyles, Typography, Button, AppBar, Toolbar, Grid, GridList, GridListTile, ListSubheader, GridListTileBar, IconButton} from '@material-ui/core';
import FormCreateTask from './components/formCreateTask';
import { connect } from 'react-redux';
import { addTaskAction } from '../../../store/actions';
import { reset } from 'redux-form';
import { ITask } from '../../../interfaces/ITask';


interface IProps{
    onAddTask(newTask : ITask) : void;
}

const useStyles = makeStyles((theme) => ({

    root : {
        flexGrow : 1,
    },
    title : {
        flexGrow: 1,
    },
    submit : {
        margin: theme.spacing(3.5, 0, 0, 2),
        textAlign: 'center',
    },
    textField :{
        textAlign: 'center',
        marginLeft: theme.spacing(3),
        color: theme.palette.text.secondary,
    },
    rootList: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        overflow: 'hidden',
        backgroundColor: theme.palette.background.paper,
    },
    gridList: {
        width: 500,
        height: 450,
    },
    icon: {
        color: 'rgba(255, 255, 255, 0.54)',
    },
}))

const mapDispatchToProps = (dispatch) => {
    return({
        onAddTask : (newTask : ITask) => {
            dispatch(addTaskAction(newTask))
            dispatch(reset('createTask'))
        }
    })
}

const mapStateToProps = (state) => ({})

export const DisplayTodo : React.FC<IProps> = (props : IProps) => {
    const classes = useStyles();

    const todoList = [
        {
            id: 0,
            title : 'покормить кота',
            isCompleted : false,
        }, 
        {
            id: 1,
            title : 'покормить ct,z',
            isCompleted : false,
        }
    ]
    return(
        <div className = {classes.root}>
            <AppBar position = "static">
                <Toolbar>
                    <Typography variant="h6" className = {classes.title}>
                        username
                    </Typography>
                    <Button color="inherit">
                        Выход
                    </Button>
                </Toolbar>
            </AppBar>
            <FormCreateTask onAddTask = {props.onAddTask}/>
            <div className={classes.rootList}>
                <GridList cellHeight={180} className={classes.gridList}>
                    <GridListTile key="Subheader" cols={2} style={{ height: 'auto' }}>
                        <ListSubheader component="div">December</ListSubheader>
                    </GridListTile>
                        {todoList.map((todo) => (
                            <GridListTile key={todo.title}>
                                <Typography variant="h6" className = {classes.title}>
                                    {todo.title}
                                </Typography>
                                <GridListTileBar
                                    title={todo.isC}
                                    subtitle={<span>by: {tile.author}</span>}
                                    actionIcon={
                                        <IconButton aria-label={`info about ${tile.title}`} className={classes.icon}>
                                            <InfoIcon />
                                        </IconButton>
                                    }
                                />
                            </GridListTile>
                        ))}
                </GridList>
            </div>

        </div>
    )
}


export default connect(mapStateToProps, mapDispatchToProps)(DisplayTodo)
