import React, { useState } from 'react';
import { makeStyles, Typography, Button, AppBar, Toolbar, Grid, GridList, GridListTile, ListSubheader, GridListTileBar, IconButton, Container, Card, CardMedia, CardContent, CardActions, CardHeader} from '@material-ui/core';
import FormCreateTask from './components/formCreateTask';
import ListTasks from './components/listTask';
import { connect } from 'react-redux';
import { addTaskAction } from '../../../store/actions';
import { reset } from 'redux-form';
import { ITask } from '../../../interfaces/ITask';
import { ITodo } from '../../../interfaces/ITodo';
import FormDialog from '../../../components/FormDialog';


interface IProps{
    onAddTask(newTask : ITask) : void;
}

const useStyles = makeStyles((theme) => ({

    root : {
        flexGrow : 1,
    },
    title : {
        flexGrow: 1,
    },
    cardGrid: {
        marginLeft : theme.spacing(3),
        width : '90%'
    },
    cardMedia: {
        display: 'block',
    },
}))

const mapDispatchToProps = (dispatch) => {
    return({
        onAddTask : (newTask : ITask) => {
            dispatch(addTaskAction(newTask))
            dispatch(reset('createTask'))
        }
    })
}

const mapStateToProps = (state) => ({})

export const DisplayTodo : React.FC<IProps> = (props : IProps) => {
    const classes = useStyles();
    const todoList : ITodo[]= [
        {
            id:1,
            user:1,
            title : '1.8.2020'
        }, 
        {
            id:3,
            user:1,
            title : '2.8.2020'
        },
        {
            id:2,
            user:1,
            title : '2.8.2020'
        }
    ]
    const [removeTodo, setRemoveTodo] = useState<ITodo>(); 

    const[open, setOpen] = useState(false);

    const handleClose = () => {
        setOpen(false)
    }

    const handleRemoveTodo = ( todo : ITodo) => {
        console.log('Задача удалена : ', todo)
        setOpen(false)
    }

    const handleOpen = (todo : ITodo) => {
        setOpen(true)
        setRemoveTodo(todo)
    }

    return(
        <div className = {classes.root}>
            <AppBar position = "static">
                <Toolbar>
                    <Typography variant="h6" className = {classes.title}>
                        username
                    </Typography>
                    <Button color="inherit">
                        Выход
                    </Button>
                </Toolbar>
            </AppBar>
            <FormCreateTask onAddTask = {props.onAddTask}/>
            <Grid container
                direction = 'row'
                justify = 'flex-start'
                alignItems = 'flex-start' 
                spacing = {1}
                className = {classes.cardGrid}
                >
                    {todoList.map((todo) => (
                        <Grid key = {todo.id} item xs={6} md={3}>
                            <Card>
                                <CardHeader
                                title = {todo.title}
                                subheader = "Ваши задачи: "
                                />
                                <CardActions className={classes.cardMedia}>
                                    <ListTasks idTodo = {todo.id!}/>
                                    <Button size = "small" color = "secondary" onClick = {() => handleOpen(todo)}>
                                        Удалить список
                                    </Button>
                                    <FormDialog 
                                        removeElement = {removeTodo!}
                                        isOpen = {open}
                                        isTask = {false}
                                        dialogTitle = 'Удалить список?'
                                        dialogContextText = "Вы действительно хотите удалить данную задачу?"
                                        handleRemoveTodo = {handleRemoveTodo}
                                        handeleClose = {handleClose}
                                    />
                                </CardActions>
                            </Card>
                        </Grid>
                    ))}
            </Grid>

        </div>
    )
}


export default connect(mapStateToProps, mapDispatchToProps)(DisplayTodo)
