import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { connectModal, InjectedProps  } from 'redux-modal';
import { ITodo } from '../../interfaces/ITodo';
import { ITask } from '../../interfaces/ITask';

interface Props  {
    removeElement : ITodo | ITask;
    dialogContextText : string;
    dialogTitle : string;
    isTask : boolean;
    handlerRemove?(task : ITask) : void;
    handleRemoveTodo?(todo : ITodo) : void;
}

const FormDialog : React.FC<Props & In>= ({removeElement, show, dialogContextText, dialogTitle, handlerRemove, handleRemoveTodo, isTask, ...props }: Props) => (
    <Dialog open = {show} aria-labelledby = "form-dialog-title">
        <DialogTitle id = "form-dialog-title">
            {dialogTitle}
        </DialogTitle>
        <DialogContent>
            <DialogContentText>
                {dialogContextText}
            </DialogContentText>
        </DialogContent>
        <DialogActions>
            <Button  color = "primary" onClick = {props.handleHide}>
                Отмена
            </Button>
            <Button color = "primary" onClick  = {() =>{isTask ? handlerRemove!((removeElement as ITask)) : handleRemoveTodo!((removeElement as ITodo))}}>
                Удалить
            </Button>
        </DialogActions>
    </Dialog>
);

const modal  =  connectModal({
    name : 'deleteRequest'
})(FormDialog);

export default modal;
