import React, { useState } from 'react'
import DeleteIcon from '@material-ui/icons/Delete';
import { makeStyles, Typography, Card, CardContent, IconButton, Checkbox} from '@material-ui/core'
import { ITodo } from '../../interface';
import FormDialog from '../components/FormDialog'



const useStyles = makeStyles((theme) => ({

    root: {
        display: 'flex',
        padding : theme.spacing(0,3,0,3),
        margin : theme.spacing(5),
      },
      details: {
        display: 'flex',
        flexDirection: 'column',
      },
      notCheck :{
        flex: '1 0 auto',
      },
      check: {
        flex: '1 0 auto',
        textDecoration : 'line-through',
        color: 'rgba(0, 0, 0, 0.12)',
        boxShadow: 'none',
      },
}))

interface TodoListProps{
    todoList : ITodo[];
    onToggle(id : number) : void;
    onRemove(id : number) : void;
}

interface IFormDialog{
    id?:number;
    open : boolean;
}
const ListTask : React.FC<TodoListProps>= ({todoList, onToggle, onRemove}) => {

    const classes = useStyles()
    const[open, setOpen] = useState<IFormDialog>()

    if(todoList.length === 0){
        return (
            <Typography variant = "h5" component = "h5" align = 'center'>
                Задач нет!
            </Typography>
        )
    }

    const handleClose = () => {
        const stateDialog : IFormDialog= {
            open : false
        }
        setOpen(stateDialog)
    }

    const handleRemoveTask = (id:number | undefined) => {
        onRemove(id)
        const stateDialog : IFormDialog= {
            id : id,
            open : false
        }
        setOpen(stateDialog)
    }

    const handleOpen = (id : number) => {
        const stateDialog : IFormDialog= {
            id : id,
            open : true
        }
        setOpen(stateDialog)
    }

    return(
        <div>
            {todoList.map(function(todo){
                let classCheked = classes.notCheck
                if(todo.completed){
                    classCheked = classes.check
                }
                return(
                    <Card key = {todo.id}  className = {classes.root}>
                        <Checkbox
                            checked = {todo.completed}
                            onChange = {() => {onToggle(todo.id)}}/>
                        <CardContent className = {classCheked}>
                            <Typography variant = "h6" component = "h6">
                                {todo.name}
                            </Typography>
                        </CardContent>
                        <IconButton aria-label = "delete" color="secondary" edge="end" onClick = {() => handleOpen(todo.id)}>
                            <DeleteIcon fontSize = "small"/>
                        </IconButton>
                        <FormDialog id = {open!.id} handlerRemove = {handleRemoveTask} show = {open!.open!} handleDestroy = {handleClose} handleHide = {handleClose} />
                    </Card>
                    )
                })}
        </div>
    )
}

export default ListTask
